import java.io.*;
import java.math.*;

class RaspberryPi
{
	public static BigInteger spartan(int power)
	{
		BigInteger input = BigInteger.TEN.pow(power);
		BigInteger output = BigInteger.ONE;
		BigInteger carriage = BigInteger.ZERO;
		int bitNumber = 0;

		while (input.compareTo(BigInteger.ONE) != 0)
		{
			//set bit in result
			if (carriage == BigInteger.ONE)
			{
				output = output.setBit(bitNumber);
			}
			bitNumber = bitNumber + 1;

			//determine carriage
			BigInteger oldCarriage = carriage;
			if (input.add(carriage).testBit(0) == true)
				carriage = BigInteger.ONE;
			else
				carriage = BigInteger.ZERO;
			
			//adjust remaining spartans
			input = input.subtract(oldCarriage).add(BigInteger.ONE).shiftRight(1);
		}
		return output;
	}
	
	public static void main (String[] args) throws java.lang.Exception
	{
		System.out.println(spartan(4).toString());
		System.out.println(spartan(12).toString());
		System.out.println(spartan(64).toString());
		System.out.println(spartan(1000).toString());
	}
}

